[{"name": "mcp__togomcp__search_rhea_entity", "input": {"query": "NADH", "limit": 100}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "NADH dehydrogenase human", "limit": 20}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "uniprot", "sparql_query": "PREFIX up: <http://purl.uniprot.org/core/>
PREFIX taxon: <http://purl.uniprot.org/taxonomy/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT DISTINCT ?protein ?name ?ecNumber
WHERE {
  ?protein a up:Protein ;
           up:organism taxon:9606 ;
           up:enzyme ?enzyme ;
           up:recommendedName ?recName .
  ?recName up:fullName ?name .
  ?enzyme up:activity ?activity .
  ?activity up:ecName ?ecNumber .

  FILTER(CONTAINS(LCASE(str(?name)), "nadh") || 
         CONTAINS(LCASE(str(?name)), "dehydrogenase"))
}
LIMIT 100"}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "NADH oxidoreductase human", "limit": 20}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "lactate dehydrogenase human", "limit": 10}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "malate dehydrogenase human", "limit": 10}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "alcohol dehydrogenase human", "limit": 10}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "aldehyde dehydrogenase human", "limit": 10}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "isocitrate dehydrogenase human", "limit": 10}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "glutamate dehydrogenase human", "limit": 10}}, {"name": "mcp__togomcp__search_uniprot_entity", "input": {"query": "glyceraldehyde-3-phosphate dehydrogenase human", "limit": 10}}]

[{"name": "mcp__togomcp__run_sparql", "input": {"dbname": "ensembl", "sparql_query": "
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dc: <http://purl.org/dc/terms/>

SELECT DISTINCT ?gene ?gene_id ?description
WHERE {
  ?gene a obo:SO_0001217 ;
        obo:SO_0000110 ?transcript ;
        dc:identifier ?gene_id ;
        obo:RO_0002162 <http://identifiers.org/taxonomy/9606> ;
        rdfs:label ?description .

  ?transcript a obo:SO_0000234 .

  ?gene obo:RO_0002525 ?location .
  ?location obo:RO_0002525 <http://rdf.ebi.ac.uk/resource/ensembl/homo_sapiens/GRCh38/17> .
}
LIMIT 100
"}}, {"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "ensembl"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "ensembl", "sparql_query": "PREFIX terms: <http://rdf.ebi.ac.uk/terms/ensembl/>
PREFIX so: <http://purl.obolibrary.org/obo/so#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX taxonomy: <http://identifiers.org/taxonomy/>

SELECT ?gene ?id ?label ?description
FROM <http://rdfportal.org/dataset/ensembl>
WHERE {
  ?gene a terms:EnsemblGene ;
        dcterms:identifier ?id ;
        rdfs:label ?label ;
        dcterms:description ?description ;
        so:part_of ?chr ;
        terms:has_biotype <http://ensembl.org/glossary/ENSGLOSSARY_0000026> ;
        obo:RO_0002162 taxonomy:9606 .
  FILTER(CONTAINS(STR(?chr), "GRCh38/17"))
}
LIMIT 100"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "ensembl", "sparql_query": "PREFIX terms: <http://rdf.ebi.ac.uk/terms/ensembl/>
PREFIX so: <http://purl.obolibrary.org/obo/so#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX taxonomy: <http://identifiers.org/taxonomy/>

SELECT (COUNT(DISTINCT ?gene) as ?count)
FROM <http://rdfportal.org/dataset/ensembl>
WHERE {
  ?gene a terms:EnsemblGene ;
        so:part_of ?chr ;
        terms:has_biotype <http://ensembl.org/glossary/ENSGLOSSARY_0000026> ;
        obo:RO_0002162 taxonomy:9606 .
  FILTER(CONTAINS(STR(?chr), "GRCh38/17"))
}"}}]

