[{"name": "mcp__bacdive__search", "input": {"query": "thermophilic growth media"}}, {"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "bacdive"}}, {"name": "mcp__ols__searchClasses", "input": {"query": "thermophilic bacteria", "pageSize": 10}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "bacdive", "sparql_query": "PREFIX schema: <https://purl.dsmz.de/schema/>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT DISTINCT ?mediumLabel (COUNT(DISTINCT ?strain) as ?strainCount)
FROM <http://rdfportal.org/dataset/bacdive>
WHERE {
  ?strain a schema:Strain ;
          dct:description ?description .
  ?description bif:contains "'thermophilic'" option (score ?sc) .

  ?medium a schema:CultureMedium ;
          schema:describesStrain ?strain ;
          rdfs:label ?mediumLabel .
}
GROUP BY ?mediumLabel
ORDER BY DESC(?strainCount)
LIMIT 50"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "bacdive", "sparql_query": "PREFIX schema: <https://purl.dsmz.de/schema/>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT ?mediumLabel ?tempStart ?tempEnd (COUNT(DISTINCT ?strain) as ?count)
FROM <http://rdfportal.org/dataset/bacdive>
WHERE {
  ?strain a schema:Strain ;
          dct:description ?description .
  ?description bif:contains "'thermophilic'" option (score ?sc) .

  ?medium a schema:CultureMedium ;
          schema:describesStrain ?strain ;
          rdfs:label ?mediumLabel .

  ?temp a schema:CultureTemperature ;
        schema:describesStrain ?strain ;
        schema:hasTemperatureRangeStart ?tempStart ;
        schema:hasTemperatureRangeEnd ?tempEnd .

  FILTER(?mediumLabel IN (
    "NUTRIENT AGAR (DSMZ Medium 1)",
    "THERMUS 162 MEDIUM (DSMZ Medium 878)",
    "ALICYCLOBACILLUS MEDIUM (DSMZ Medium 402)",
    "THERMOANAEROBACTER MEDIUM (DSMZ Medium 61)",
    "SULFOLOBUS MEDIUM (DSMZ Medium 88)",
    "CALDICELLULOSIRUPTOR MEDIUM (DSMZ Medium 640)",
    "CASTENHOLZ MEDIUM (DSMZ Medium 86)",
    "BACILLUS ACIDOCALDARIUS MEDIUM (DSMZ Medium 13)"
  ))
}
GROUP BY ?mediumLabel ?tempStart ?tempEnd
ORDER BY ?mediumLabel ?tempStart
LIMIT 50"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "bacdive", "sparql_query": "PREFIX schema: <https://purl.dsmz.de/schema/>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT DISTINCT ?mediumLabel ?mediaLink
FROM <http://rdfportal.org/dataset/bacdive>
WHERE {
  ?strain a schema:Strain ;
          dct:description ?description .
  ?description bif:contains "'thermophilic'" option (score ?sc) .

  ?medium a schema:CultureMedium ;
          schema:describesStrain ?strain ;
          rdfs:label ?mediumLabel .

  OPTIONAL { ?medium schema:hasMediaLink ?mediaLink }

  FILTER(CONTAINS(LCASE(?mediumLabel), "therm") || 
         CONTAINS(LCASE(?mediumLabel), "caldice") ||
         CONTAINS(LCASE(?mediumLabel), "sulfolobus") ||
         CONTAINS(LCASE(?mediumLabel), "bacillus acidocaldarius"))
}
ORDER BY ?mediumLabel
LIMIT 30"}}]

